package Ifana_4;

public class Raina_45 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	int Counter =0;
	System.out.println("Counter is : " + Counter);
	System.out.println("++Counter is : " + ++Counter);
	System.out.println("Now Counter is : " + Counter);
	
	Counter =0;
	System.out.println("Counter is : " + Counter);
	System.out.println("Counter++ is : " + Counter++);
	System.out.println("Now Counter is : " + Counter);
	
	
	}

}
